// LoadBuffEffects allows an eager lookup of values, cached into the
// loaded structs of the objects. This is for a 1-M or N-M relationship.
func (buffL) LoadBuffEffects(ctx context.Context, e boil.ContextExecutor, singular bool, maybeBuff interface{}, mods queries.Applicator) error {
	var slice []*Buff
	var object *Buff

	if singular {
		var ok bool
		object, ok = maybeBuff.(*Buff)
		if !ok {
			object = new(Buff)
			ok = queries.SetFromEmbeddedStruct(&object, &maybeBuff)
			if !ok {
				return errors.New(fmt.Sprintf("failed to set %T from embedded struct %T", object, maybeBuff))
			}
		}
	} else {
		s, ok := maybeBuff.(*[]*Buff)
		if ok {
			slice = *s
		} else {
			ok = queries.SetFromEmbeddedStruct(&slice, maybeBuff)
			if !ok {
				return errors.New(fmt.Sprintf("failed to set %T from embedded struct %T", slice, maybeBuff))
			}
		}
	}

	args := make(map[interface{}]struct{})
	if singular {
		if object.R == nil {
			object.R = &buffR{}
		}
		args[object.ID] = struct{}{}
	} else {
		for _, obj := range slice {
			if obj.R == nil {
				obj.R = &buffR{}
			}
			args[obj.ID] = struct{}{}
		}
	}

	if len(args) == 0 {
		return nil
	}

	argsSlice := make([]interface{}, len(args))
	i := 0
	for arg := range args {
		argsSlice[i] = arg
		i++
	}

	query := NewQuery(
		qm.From(`buff_effects`),
		qm.WhereIn(`buff_effects.buff_id in ?`, argsSlice...),
		qmhelper.WhereIsNull(`buff_effects.deleted_at`),
	)
	if mods != nil {
		mods.Apply(query)
	}

	results, err := query.QueryContext(ctx, e)
	if err != nil {
		return errors.Wrap(err, "failed to eager load buff_effects")
	}

	var resultSlice []*BuffEffect
	if err = queries.Bind(results, &resultSlice); err != nil {
		return errors.Wrap(err, "failed to bind eager loaded slice buff_effects")
	}

	if err = results.Close(); err != nil {
		return errors.Wrap(err, "failed to close results in eager load on buff_effects")
	}
	if err = results.Err(); err != nil {
		return errors.Wrap(err, "error occurred during iteration of eager loaded relations for buff_effects")
	}

	if len(buffEffectAfterSelectHooks) != 0 {
		for _, obj := range resultSlice {
			if err := obj.doAfterSelectHooks(ctx, e); err != nil {
				return err
			}
		}
	}
	if singular {
		object.R.BuffEffects = resultSlice
		for _, foreign := range resultSlice {
			if foreign.R == nil {
				foreign.R = &buffEffectR{}
			}
			foreign.R.Buff = object
		}
		return nil
	}

	for _, foreign := range resultSlice {
		for _, local := range slice {
			if local.ID == foreign.BuffID {
				local.R.BuffEffects = append(local.R.BuffEffects, foreign)
				if foreign.R == nil {
					foreign.R = &buffEffectR{}
				}
				foreign.R.Buff = local
				break
			}
		}
	}

	return nil
}

