// AddBuffEffects adds the given related objects to the existing relationships
// of the buff, optionally inserting them as new records.
// Appends related to o.R.BuffEffects.
// Sets related.R.Buff appropriately.
func (o *Buff) AddBuffEffects(ctx context.Context, exec boil.ContextExecutor, insert bool, related ...*BuffEffect) error {
	var err error
	for _, rel := range related {
		if insert {
			rel.BuffID = o.ID
			if err = rel.Insert(ctx, exec, boil.Infer()); err != nil {
				return errors.Wrap(err, "failed to insert into foreign table")
			}
		} else {
			updateQuery := fmt.Sprintf(
				"UPDATE \"buff_effects\" SET %s WHERE %s",
				strmangle.SetParamNames("\"", "\"", 1, []string{"buff_id"}),
				strmangle.WhereClause("\"", "\"", 2, buffEffectPrimaryKeyColumns),
			)
			values := []interface{}{o.ID, rel.ID}

			if boil.IsDebug(ctx) {
				writer := boil.DebugWriterFrom(ctx)
				fmt.Fprintln(writer, updateQuery)
				fmt.Fprintln(writer, values)
			}
			if _, err = exec.ExecContext(ctx, updateQuery, values...); err != nil {
				return errors.Wrap(err, "failed to update foreign table")
			}

			rel.BuffID = o.ID
		}
	}

	if o.R == nil {
		o.R = &buffR{
			BuffEffects: related,
		}
	} else {
		o.R.BuffEffects = append(o.R.BuffEffects, related...)
	}

	for _, rel := range related {
		if rel.R == nil {
			rel.R = &buffEffectR{
				Buff: o,
			}
		} else {
			rel.R.Buff = o
		}
	}
	return nil
}

