// If selectCols is empty Find will return all columns.
func FindBuff(ctx context.Context, exec boil.ContextExecutor, iD string, selectCols ...string) (*Buff, error) {
	buffObj := &Buff{}

	sel := "*"
	if len(selectCols) > 0 {
		sel = strings.Join(strmangle.IdentQuoteSlice(dialect.LQ, dialect.RQ, selectCols), ",")
	}
	query := fmt.Sprintf(
		"select %s from \"buffs\" where \"id\"=$1 and \"deleted_at\" is null", sel,
	)

	q := queries.Raw(query, iD)

	err := q.Bind(ctx, exec, buffObj)
	if err != nil {
		if errors.Is(err, sql.ErrNoRows) {
			return nil, sql.ErrNoRows
		}
		return nil, errors.Wrap(err, "entity: unable to select from buffs")
	}

	if err = buffObj.doAfterSelectHooks(ctx, exec); err != nil {
		return buffObj, err
	}

	return buffObj, nil
}

